//Set local timezone offset for adjusting it in the "Initiate session" in this branch of the flow.
//The value of tzOffset is a positiive or negative number in relation UTC to your local time offset
//i.e. BST wll return -60 mins as UTC is 60 mins behind BST
var currentDate = new Date();
var tzOffset = currentDate.getTimezoneOffset();

//if returned offset is positive number convert to negative as it needs to be subtracted from SZ timestamps
if(tzOffset > 0){
flow.set("tzOffsetAdjust", -Math.abs(tzOffset)+120);
}

//if returned offset is negative number convert to positive as it needs to be added to SZ timestamps
if(tzOffset < 0){
//flow.set("tzOffsetAdjust", -tzOffset);
flow.set("tzOffsetAdjust", Math.abs(tzOffset)+120);
}
node.warn(flow.get("tzOffsetAdjust"))
//flow.set("tzOffsetAdjust", 0);

