msg.headers["Content-Type"]="application/json;charset=UTF-8";

msg.payload = {
    "username": flow.get("loginName"),
    "password": flow.get("loginPass")
};
return msg;

var response = msg.payload;
var responseCode = msg.statusCode

//Check that SZ HTTP connection and login was succesful
