//This first section deals with automaticaly setting the local timezone offset in  "timeZoneUtcOffset" from UTC.
//It uses the daylight savings offset calculated in the node "Get service ticket/set variables".

//Set the flag "negoffSet" default value to false i.e. it is a positive number
context.set("negoffSet", false);

var offSet = (flow.get("tzOffsetAdjust") * 60); //convert the returned minute value variable into seconds

//If returned offSet valeu is negative then convert it to positiive to remove the "-" sign and flag it to be added back later
if(offSet < 0){
offSet = Math.abs(offSet);
context.set("negoffSet", true);
}

//See node "Set System Info" for info on how the calcs below work
var hour = Math.floor(offSet / (60*60));
      offSet -= hour   * (60*60);

var minute = Math.floor(offSet / (60));
      offSet -= minute * (60);

var offSetFormatted = (hour.toString().padStart(2, '0')+":"+minute.toString().padStart(2, '0'));

//If the original offSet value was negative then ada "-" prefix to hh:mm formatted offSet value, else add a "+"
if(context.get("negoffSet") === true){
    offSetFormatted = "-".concat(offSetFormatted);
}
else {
    offSetFormatted = "+".concat(offSetFormatted);
}

