//Define a function to convert returned SZ data timestamp into seconds and apply local time including DST.
function adjustTime() {
    //Convert the timestamp from returned SZ data to UTC in seconds (the variable used is clientCountTime).
    clientCountTime = ((new Date(clientCountTime)).getTime());

    //Adjust time SZ clientCountTime to local time including DST by using thetzOffsetAdjust value calculated in the node "Get service ticket/set variables"
    var offSet = (flow.get("tzOffsetAdjust") * 60 * 1000); //turn the returned minute value variable into ms

    //If returned offset is negative subtract it from the SZ clientCountTime
    if(offSet < 0){
        clientCountTime = clientCountTime - offSet;
    }
    //If returned offset is positive subtract it from the SZ clientCountTime
    if(offSet > 0){
        clientCountTime = clientCountTime + offSet;
    }  
} //End of the function

