function sliceBuf(buf, start, end) {
    return buf.slice(start,end).readInt32BE();
}


if (msg.topic.endsWith("MGR")){
    if (msg.payload[0]== 2 && msg.payload[1]== 1){
        var bytes = msg.payload.slice(4,68)
        var result = ""
        for (var i = 0; i < bytes.length; i++) {
            result += String.fromCharCode(parseInt(bytes[i]));
        }
        node.warn("Controller name: " + result)
        bytes = msg.payload.slice(68,84)
        result = ""
