            result += String.fromCharCode(parseInt(bytes[i]));
        }
        node.warn("API version: " + result)
        bytes = msg.payload.slice(84,100)
        result = ""
        for (i = 0; i < bytes.length; i++) {
            result += String.fromCharCode(parseInt(bytes[i]));
        }
        node.warn("Controller version: " + result)
    }
    else{
        if (msg.payload[0]== 2 && msg.payload[1]== 7){
