        for (i= 0; i < qtdeVenues; i++){
                    bytes= msg.payload.slice(6+(16*i),22+(16*i))
                    for (var j = 0; j < bytes.length; j++) {
                        result += String.fromCharCode(parseInt(bytes[j]))
                    }
            node.warn("Venue name: " + result)
            result=""
            }
        }
    }
}

